import java.util.*;
public class KgtoLb {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		Scanner sc = new Scanner(System.in);
		
		System.out.println("enter the weight ");
		
		
		int kg = sc.nextInt();
		
		System.out.println("your weight is: " +kg + " kg" );
		
		// kg into pound 
		
		double pound = (int) (kg * 2.205);
		
		System.out.println(pound);
		
		
		System.out.println("your weight in pound is : "+pound + " lb " );
		
		
		
		
		
		
		
		
		
		
		

	}

}
